void ui_event_UpTempTarget(lv_event_t * e)
{
    lv_event_code_t event_code = lv_event_get_code(e);

    if(event_code == LV_EVENT_PRESSED) {
        upTempEvent(e);
    }
    if(event_code == LV_EVENT_LONG_PRESSED) {
        upTempEvent(e);
    }
    if(event_code == LV_EVENT_LONG_PRESSED_REPEAT) {
        upTempEvent(e);
    }
    if(event_code == LV_EVENT_RELEASED) {
        upTempEventFinished(e);
    }
}

void ui_event_DownTempTarget(lv_event_t * e)
{
    lv_event_code_t event_code = lv_event_get_code(e);

    if(event_code == LV_EVENT_PRESSED) {
        downTempEvent(e);
    }
    if(event_code == LV_EVENT_LONG_PRESSED) {
        downTempEvent(e);
    }
    if(event_code == LV_EVENT_LONG_PRESSED_REPEAT) {
        downTempEvent(e);
    }
    if(event_code == LV_EVENT_RELEASED) {
        downTempEventFinished(e);
    }
}

void ui_event_SettingsTarget(lv_event_t * e)
{
    lv_event_code_t event_code = lv_event_get_code(e);

    if(event_code == LV_EVENT_CLICKED) {
        _ui_screen_change(&ui_SettingsScreen, LV_SCR_LOAD_ANIM_NONE, 500, 0, &ui_SettingsScreen_screen_init);
        initializeSettingsScreen(e);
    }
}

